import React, { useState, useEffect } from 'react';
import { Box, Grid, Paper, Typography, Card, CardContent } from '@mui/material';
import { LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import axios from 'axios';
import './App.css';

// 修复Leaflet默认图标问题
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const DISPATCH_CENTER_URL = 'http://localhost:8001';

function App() {
  const [statistics, setStatistics] = useState({
    total_orders_today: 0,
    active_vehicles: 0,
    delivered_today: 0,
    pending_orders: 0
  });
  const [orders, setOrders] = useState([]);
  const [vehicles, setVehicles] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [demandPredictions, setDemandPredictions] = useState([]);
  const [ws, setWs] = useState(null);

  useEffect(() => {
    // 连接WebSocket
    const websocket = new WebSocket(`ws://localhost:8001/ws`);

    websocket.onopen = () => {
      console.log('WebSocket连接已建立');
      setWs(websocket);
    };

    websocket.onmessage = (event) => {
      const data = JSON.parse(event.data);

      if (data.type === 'initial_state') {
        setStatistics(data.data.statistics || statistics);
        setOrders(Object.values(data.data.orders || {}));
        setVehicles(Object.values(data.data.vehicles || {}));
        setAlerts(data.data.alerts || []);
        setDemandPredictions(Object.values(data.data.demand_predictions || {}));
      } else if (data.type === 'order_assigned') {
        // 更新订单状态
        fetchOrders();
      } else if (data.type === 'alert') {
        setAlerts(prev => [data.data, ...prev].slice(0, 50));
      }
    };

    websocket.onerror = (error) => {
      console.error('WebSocket错误:', error);
    };

    websocket.onclose = () => {
      console.log('WebSocket连接已关闭');
    };

    // 定期获取数据
    const fetchData = async () => {
      try {
        const [statsRes, ordersRes, vehiclesRes, alertsRes, predictionsRes] = await Promise.all([
          axios.get(`${DISPATCH_CENTER_URL}/api/v1/statistics`),
          axios.get(`${DISPATCH_CENTER_URL}/api/v1/orders`),
          axios.get(`${DISPATCH_CENTER_URL}/api/v1/vehicles`),
          axios.get(`${DISPATCH_CENTER_URL}/api/v1/alerts?limit=10`),
          axios.get(`${DISPATCH_CENTER_URL}/api/v1/demand-predictions`)
        ]);

        setStatistics(statsRes.data);
        setOrders(ordersRes.data);
        setVehicles(vehiclesRes.data);
        setAlerts(alertsRes.data);
        setDemandPredictions(predictionsRes.data);
      } catch (error) {
        console.error('获取数据失败:', error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000); // 每5秒更新

    return () => {
      websocket.close();
      clearInterval(interval);
    };
  }, []);

  // 准备图表数据
  const orderTrendData = orders.slice(-20).map((order, index) => ({
    time: index,
    orders: 1
  }));

  const vehicleStatusData = [
    { status: '空闲', count: vehicles.filter(v => v.status === 'IDLE').length },
    { status: '装载中', count: vehicles.filter(v => v.status === 'LOADING').length },
    { status: '运输中', count: vehicles.filter(v => v.status === 'IN_TRANSIT').length },
    { status: '配送中', count: vehicles.filter(v => v.status === 'DELIVERING').length },
  ];

  return (
    <Box sx={{ flexGrow: 1, p: 3, backgroundColor: '#f5f5f5', minHeight: '100vh' }}>
      <Typography variant="h4" component="h1" gutterBottom sx={{ mb: 3, fontWeight: 'bold' }}>
        亚马逊物流网络实时智能调度系统
      </Typography>

      {/* 统计卡片 */}
      <Grid container spacing={3} sx={{ mb: 3 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                今日订单总数
              </Typography>
              <Typography variant="h4">
                {statistics.total_orders_today}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                活跃车辆
              </Typography>
              <Typography variant="h4">
                {statistics.active_vehicles}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                今日已送达
              </Typography>
              <Typography variant="h4">
                {statistics.delivered_today}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                待处理订单
              </Typography>
              <Typography variant="h4" color="error">
                {statistics.pending_orders}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* 图表和地图 */}
      <Grid container spacing={3}>
        {/* 订单趋势 */}
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              订单趋势
            </Typography>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={orderTrendData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="time" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="orders" stroke="#8884d8" />
              </LineChart>
            </ResponsiveContainer>
          </Paper>
        </Grid>

        {/* 车辆状态分布 */}
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              车辆状态分布
            </Typography>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={vehicleStatusData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="status" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="count" fill="#82ca9d" />
              </BarChart>
            </ResponsiveContainer>
          </Paper>
        </Grid>

        {/* 地图 */}
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 2, height: 400 }}>
            <Typography variant="h6" gutterBottom>
              实时车辆位置
            </Typography>
            <MapContainer
              center={[39.9042, 116.4074]}
              zoom={10}
              style={{ height: '350px', width: '100%' }}
            >
              <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              />
              {vehicles.map((vehicle) => (
                vehicle.latitude && vehicle.longitude && (
                  <Marker
                    key={vehicle.vehicle_id}
                    position={[vehicle.latitude, vehicle.longitude]}
                  >
                    <Popup>
                      <div>
                        <strong>车辆ID:</strong> {vehicle.vehicle_id}<br />
                        <strong>状态:</strong> {vehicle.status}<br />
                        <strong>速度:</strong> {vehicle.speed_kmh?.toFixed(1)} km/h<br />
                        <strong>载货量:</strong> {vehicle.current_capacity}/{vehicle.max_capacity}
                      </div>
                    </Popup>
                  </Marker>
                )
              ))}
            </MapContainer>
          </Paper>
        </Grid>

        {/* 告警列表 */}
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2, height: 400, overflow: 'auto' }}>
            <Typography variant="h6" gutterBottom>
              实时告警
            </Typography>
            {alerts.length === 0 ? (
              <Typography color="textSecondary">暂无告警</Typography>
            ) : (
              alerts.map((alert, index) => (
                <Card key={index} sx={{ mb: 1, bgcolor: alert.severity === 'CRITICAL' ? '#ffebee' : '#fff3e0' }}>
                  <CardContent>
                    <Typography variant="subtitle2" color="error">
                      {alert.severity}
                    </Typography>
                    <Typography variant="body2">
                      {alert.data?.description || alert.data?.anomaly_type || '未知告警'}
                    </Typography>
                    <Typography variant="caption" color="textSecondary">
                      {new Date(alert.timestamp).toLocaleString()}
                    </Typography>
                  </CardContent>
                </Card>
              ))
            )}
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
}

export default App;
